import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import * as Iocons from 'react-icons/io';
import { SideMenuData } from './SideMenuData';
import CrystaLogo from './CrystaLogo.svg';

function SideMenu() {
    const [sidebar, setSidebar] = useState(false);

    const showSidebar = () => setSidebar(!sidebar);
    return (
        <>
            <div className = 'SideMenu' class = " flex items-center bg-gray-50 shadow-md h-9 w-screen rounded-r-lg">

                <Link to="#" className = 'open-menu-arrow' class = "flex m-3 text-lg flex">
                    <Iocons.IoIosArrowForward onClick ={showSidebar}/>
                </Link>

                <img src = {CrystaLogo} alt = "CrystaLogo" class = "content-between"/>

            </div>
            <nav className={sidebar ? 'side-menu active' : 'side-menu'} class = "flex justify-center bg-gray-50 h-5/6 w-64 rounded-br-3xl shadow-md">
                <ul className = 'side-menu-items'>
                    <li className = "SideMenu-toggle">
                        <Link to = "#" className = 'open-menu-arrow'>
                            <Iocons.IoIosArrowBack />
                        </Link> 
                    </li>
                    {SideMenuData.map((item, index) => {
                        return (
                            <li key={index} className={item.cName}>
                                <Link to = {item.path}>
                                    {item.icon}
                                    <span>{item.title}</span>
                                </Link>
                            </li>
                        )
                    })}
                </ul>    
            </nav>
        </>
    );
}

export default SideMenu;
