import './index.css';
import SideMenu from './components/SideMenu';
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Calendar from './pages/Calendar';
import Tasks from './pages/Tasks';
import Energy from './pages/Energy';
import Logout from './pages/Logout';


function App() {
    (window).focus(function () {
      //do something      
       console.log("You are in this tab");  
    });

    (window).blur(function () {
      //do something
       console.log("You left this tab");
    })

  return (
    <>
      <Router>
        <SideMenu/>
        <Switch>
          <Route path='/' exact component={Energy}/>
          <Route path='/pages/Calendar' component={Calendar}/>
          <Route path='/pages/Tasks' component={Tasks}/>
          <Route path='/pages/Energy' component={Energy}/>
          <Route path='/pages/Logout'  component={Logout}/>
        </Switch>
      </Router>
    </>
  );
}

export default App;
